username=#{userName}, password=#{password}, user_alias=#{userAlias}, sex=#{sex}, cell_phone_number=#{cellPhoneNumber}, address=#{address}, entry_date=#{entryDate}, last_login=#{lastLogin}, is_super=#{isSuper}, email=#{email}, modify_time=#{modifyTime}, is_enable=#{isEnable}, remarks=#{remarks}
where user_id=#{userId}

