password=#{newPassword} where
user_id=#{userId}

update users set
password=#{password} where
user_id=#{userId}

mapper.select:
select
user_id,username,user_alias,sex,cell_phone_number,address,entry_date,last_login,is_super,email,create_time,modify_time,modify_time,is_enable,remarks
from
users


		user_id=#{userId}


		OR username=#{userName}

select
user_id,username,user_alias,sex,cell_phone_number,address,entry_date,last_login,is_super,email,create_time,modify_time,is_enable,remarks
from
users


		is_enable=#{isEnable}


		and is_super=#{isSuper}

SELECT u.user_id user_id,u.username username,'无' post_name,u.sex sex,u.cell_phone_number cell_phone_number,u.email email,u.is_enable is_enable,u.is_super is_super from user_organization uo JOIN users u ON uo.user_id=u.user_id WHERE uo.org_id in #{orgId} AND u.username like CONCAT('%',#{username},'%')
AND u.is_enable = #{isEnable}
union SELECT u.user_id user_id,u.username username,p.post_name post_name,u.sex sex,u.cell_phone_number cell_phone_number,u.email email,u.is_enable is_enable,u.is_super is_super from user_organization uo JOIN users u ON uo.user_id=u.user_id join user_post up on up.user_id=u.user_id join post p on p.post_id=up.post_id WHERE uo.org_id in #{orgId} AND u.username like CONCAT('%',#{username},'%')
AND u.is_enable = #{isEnable}
UNION SELECT u1.user_id,u1.username username,p.post_name post_name,u1.sex sex,u1.cell_phone_number cell_phone_number,u1.email email,u1.is_enable is_enable,u1.is_super is_super from user_post up JOIN users u1 ON up.user_id=u1.user_id join post p on p.post_id=up.post_id WHERE up.post_id in(SELECT post_id FROM post where org_id in
#{orgId}
AND u1.username like "%"#{username}"%"
AND u1.is_enable = #{isEnable}
) limit #{page.beginRow},#{page.pageSize}

select count(*) from ( SELECT u.username username from user_organization uo JOIN users u ON uo.user_id=u.user_id WHERE uo.org_id in #{orgId} AND u.username like CONCAT('%',#{username},'%')
AND u.is_enable = #{isEnable}
UNION SELECT u1.username username from user_post up JOIN users u1 ON up.user_id=u1.user_id WHERE up.post_id in(SELECT post_id FROM post where org_id in
#{orgId}
AND u1.username like CONCAT('%',#{username},'%')
AND u1.is_enable = #{isEnable}
)) u2

SELECT u1.user_id user_id,u1.username username,u1.sex sex,u1.cell_phone_number cell_phone_number,u1.email email,u1.is_enable is_enable,u1.is_super is_super,p.post_name post_name from user_post up JOIN users u1 ON up.user_id=u1.user_id join post p on p.post_id=up.post_id WHERE up.post_id = #{postId}
AND u1.username like CONCAT('%',#{username},'%')
AND u1.is_enable = #{isEnable}
limit #{page.beginRow},#{page.pageSize}

SELECT count(*) from user_post
up JOIN users u1 ON
up.user_id=u1.user_id WHERE up.post_id
= #{postId}

	AND u1.username like CONCAT('%',#{username},'%')


	AND u1.is_enable = #{isEnable}

SELECT u.username username,u.email email,u.cell_phone_number cell_phone_number ,u.address address,u.user_alias user_alias,concat(o.org_name,'-',p.post_name) post_name,u.create_time create_time,u.modify_time modify_time, u.entry_date entry_date,u.last_login last_login,u.is_enable is_enable,u.sex sex,u.remarks remarks from users u JOIN user_post up on u.user_id=up.user_id JOIN post p on up.post_id=p.post_id JOIN organization o ON o.org_id=p.org_id
u.user_id=#{userId}
u.username=#{userName}
union SELECT u.username username,u.email email,u.cell_phone_number cell_phone_number ,u.address address,u.user_alias user_alias,'无' post_name,u.create_time create_time,u.modify_time modify_time, u.entry_date entry_date,u.last_login last_login,u.is_enable is_enable,u.sex sex,u.remarks remarks from users u
u.user_id=#{userId}
u.username=#{userName}
